import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cartas, setCartas] = useState([]);

  // Obtener las cartas desde el backend al cargar la p√°gina
  useEffect(() => {
    fetch('http://localhost:5000/cartas')
      .then(res => res.json())
      .then(data => setCartas(data))
      .catch(err => console.error('Error al obtener cartas:', err));
  }, []);

  return (
    <div className="container py-5">
      <h1 className="text-center mb-5 fw-bold text-danger">
        üÉè Tienda Pok√©mon
      </h1>

      {cartas.length === 0 ? (
        <p className="text-center text-muted">No hay cartas disponibles.</p>
      ) : (
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {cartas.map((carta) => (
            <div key={carta.id} className="col">
              <div className="card h-100 shadow border-0">
                <img
                  src={carta.imagen}
                  className="card-img-top"
                  alt={carta.nombre}
                  style={{ height: '350px', objectFit: 'cover' }}
                />
                <div className="card-body text-center">
                  <h5 className="card-title text-primary">{carta.nombre}</h5>
                  <p className="card-text mb-1"><strong>Tipo:</strong> {carta.tipo}</p>
                  <p className="card-text mb-1"><strong>Rareza:</strong> {carta.rareza}</p>
                  <p className="card-text"><strong>Stock:</strong> {carta.cantidad}</p>
                </div>
                <div className="card-footer bg-dark text-white text-center">
                  üí∞ <strong>${carta.valor}</strong>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
