import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cartas, setCartas] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/cartas')
      .then(res => res.json())
      .then(data => setCartas(data))
      .catch(err => console.error('Error al obtener cartas:', err));
  }, []);

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4 fw-bold text-danger">üÉè Tienda Pok√©mon</h1>
      <p className="text-center text-muted mb-5">
        Explora nuestras cartas coleccionables ‚Äî elige tu Pok√©mon favorito üî•
      </p>

      {cartas.length === 0 ? (
        <p className="text-center text-muted">No hay cartas disponibles.</p>
      ) : (
        <div className="row g-4 justify-content-center">
          {cartas.map((carta) => (
            <div key={carta.id} className="col-6 col-sm-4 col-md-3 col-lg-2">
              <div
                className="card h-100 border-0 shadow-sm"
                style={{
                  borderRadius: '15px',
                  transition: 'transform 0.2s ease, box-shadow 0.2s ease',
                  overflow: 'hidden',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = 'scale(1.05)';
                  e.currentTarget.style.boxShadow = '0 8px 20px rgba(0,0,0,0.3)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = 'scale(1)';
                  e.currentTarget.style.boxShadow = '0 4px 10px rgba(0,0,0,0.1)';
                }}
              >
                <img
                  src={carta.imagen}
                  className="card-img-top"
                  alt={carta.nombre}
                  style={{
                    height: '180px',
                    objectFit: 'cover',
                    borderTopLeftRadius: '15px',
                    borderTopRightRadius: '15px',
                  }}
                />
                <div className="card-body p-2 text-center">
                  <h6 className="card-title text-primary fw-bold mb-1">
                    {carta.nombre}
                  </h6>
                  <p className="card-text mb-0" style={{ fontSize: '0.85rem' }}>
                    <strong>Tipo:</strong> {carta.tipo}
                  </p>
                  <p className="card-text mb-1" style={{ fontSize: '0.85rem' }}>
                    <strong>Rareza:</strong> {carta.rareza}
                  </p>
                  <p className="card-text mb-2" style={{ fontSize: '0.8rem' }}>
                    <strong>Stock:</strong> {carta.cantidad}
                  </p>
                </div>
                <div
                  className="card-footer text-center text-white fw-bold"
                  style={{
                    backgroundColor: '#2c2c2c',
                    borderBottomLeftRadius: '15px',
                    borderBottomRightRadius: '15px',
                    fontSize: '0.9rem',
                  }}
                >
                  üí∞ ${carta.valor}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
