import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles.css';

function App() {
  const [cartas, setCartas] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/cartas')
      .then(res => res.json())
      .then(data => setCartas(data))
      .catch(err => console.error('Error al obtener cartas:', err));
  }, []);

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4 text-danger">üÉè Tienda Pok√©mon</h1>
      <p className="text-center text-muted mb-5">
        Explora nuestras cartas coleccionables ‚Äî elige tu Pok√©mon favorito üî•
      </p>

      {cartas.length === 0 ? (
        <p className="text-center text-muted">No hay cartas disponibles.</p>
      ) : (
        <div className="row row-cols-2 row-cols-sm-3 row-cols-md-4 row-cols-lg-5 g-4 justify-content-center">
          {cartas.map((carta) => (
            <div key={carta.id} className="col">
              <div className="card h-100 shadow-sm">
                <img
                  src={carta.imagen}
                  className="card-img-top"
                  alt={carta.nombre}
                />
                <div className="card-body">
                  <h6 className="card-title">{carta.nombre}</h6>
                  <p className="card-text"><strong>Tipo:</strong> {carta.tipo}</p>
                  <p className="card-text"><strong>Rareza:</strong> {carta.rareza}</p>
                  <p className="card-text"><strong>Stock:</strong> {carta.cantidad}</p>
                </div>
                <div className="card-footer text-center">
                  üí∞ ${carta.valor}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
