import { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [cartas, setCartas] = useState([]);
  const [nuevaCarta, setNuevaCarta] = useState({
    nombre: '',
    tipo: '',
    rareza: '',
    cantidad: '',
    valor: '',
    imagen: ''
  });

  useEffect(() => {
    fetch('http://localhost:5000/cartas')
      .then(res => res.json())
      .then(data => setCartas(data))
      .catch(err => console.error('Error al obtener cartas:', err));
  }, []);

  const agregarCarta = async () => {
    const { nombre, tipo, rareza, cantidad, valor, imagen } = nuevaCarta;
    if (!nombre || !tipo || !rareza || !cantidad || !valor || !imagen) {
      alert('Completa todos los campos');
      return;
    }

    await fetch('http://localhost:5000/cartas', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ nombre, tipo, rareza, cantidad, valor, imagen }),
    });

    const res = await fetch('http://localhost:5000/cartas');
    const data = await res.json();
    setCartas(data);
    setNuevaCarta({ nombre: '', tipo: '', rareza: '', cantidad: '', valor: '', imagen: '' });
  };

  return (
    <div className="container py-4">
      <h1 className="text-center mb-4 text-danger fw-bold">üÉè Tienda Pok√©mon</h1>

      {/* Formulario para agregar nuevas cartas */}
      <div className="card p-4 shadow-sm mb-5">
        <div className="row g-3">
          <div className="col-md-2">
            <input
              className="form-control"
              placeholder="Nombre"
              value={nuevaCarta.nombre}
              onChange={e => setNuevaCarta({ ...nuevaCarta, nombre: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <input
              className="form-control"
              placeholder="Tipo"
              value={nuevaCarta.tipo}
              onChange={e => setNuevaCarta({ ...nuevaCarta, tipo: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <input
              className="form-control"
              placeholder="Rareza"
              value={nuevaCarta.rareza}
              onChange={e => setNuevaCarta({ ...nuevaCarta, rareza: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <input
              type="number"
              className="form-control"
              placeholder="Cantidad"
              value={nuevaCarta.cantidad}
              onChange={e => setNuevaCarta({ ...nuevaCarta, cantidad: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <input
              type="number"
              className="form-control"
              placeholder="Valor ($)"
              value={nuevaCarta.valor}
              onChange={e => setNuevaCarta({ ...nuevaCarta, valor: e.target.value })}
            />
          </div>
          <div className="col-md-2">
            <input
              className="form-control"
              placeholder="URL Imagen"
              value={nuevaCarta.imagen}
              onChange={e => setNuevaCarta({ ...nuevaCarta, imagen: e.target.value })}
            />
          </div>
        </div>
        <button className="btn btn-success mt-3" onClick={agregarCarta}>
          ‚ûï Agregar Carta
        </button>
      </div>

      {/* Cat√°logo de cartas */}
      <div className="row row-cols-1 row-cols-md-3 g-4">
        {cartas.map((carta) => (
          <div key={carta.id} className="col">
            <div className="card h-100 shadow">
              <img src={carta.imagen} className="card-img-top" alt={carta.nombre} />
              <div className="card-body">
                <h5 className="card-title text-center text-primary">{carta.nombre}</h5>
                <p className="card-text"><strong>Tipo:</strong> {carta.tipo}</p>
                <p className="card-text"><strong>Rareza:</strong> {carta.rareza}</p>
                <p className="card-text"><strong>Cantidad:</strong> {carta.cantidad}</p>
              </div>
              <div className="card-footer bg-dark text-white text-center">
                üí∞ <strong>${carta.valor}</strong>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
